<?php if (!defined('THINK_PATH')) exit();?><h2 class="record-title meal<?php echo $_GET['idc']; ?>" id="ab<?php echo $_GET['idc']; ?>" onclick="choose_one_time(<?php echo $_GET['idc']; ?>)"><?php if((int) $_GET['idc'] == '1'){echo '早餐';}else if((int) $_GET['idc'] == '6'){echo '午餐';}else if((int) $_GET['idc'] == '2'){echo '晚餐';}else if((int) $_GET['idc'] == '7'){echo '零食';} ?><span> (<b class="suny_data_<?php echo $_GET['idc']; ?>"><?php echo ($liste["total_price"]); ?></b>大卡)</span></h2><div class="record-body"><?php if(is_array($listz)): $k = 0; $__LIST__ = $listz;if( count($__LIST__)==0 ) : echo "$empty" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($k % 2 );++$k;?><dl><dd><span class="zl"><?php echo ($vo["name"]); ?></span><div class="varietiesbox"><div class="log" onclick="showDiv(this)"><span><?php if($vo['unit'] == 'G'): echo ($vo["num"]); ?>/100克<?php else: echo ($vo["num"]); ?>/<?php echo ($vo["unit"]); endif; ?></span><a href="javascript:void(0);"></a></div><div class="arrowDown"  style="display:none ; "><span class="fl_<?php echo $_GET['idc']; ?>_<?php echo ($vo["id"]); ?>"><b>数量：</b><input type="text" name="quatily[<?php echo ($k); ?>]" value="<?php echo ($vo["num"]); ?>" id="<?php echo $_GET['idc']; ?>book_num<?php echo ($k); ?>" /><a class="j" href="javascript:void(0);" onClick="addnum_<?php echo $_GET['idc']; ?>('<?php echo ($k); ?>')"></a><a class="c" href="javascript:void(0);" onClick="subnum_<?php echo $_GET['idc']; ?>('<?php echo ($k); ?>')"></a></span><div><input type="button" onclick="update(<?php echo ($vo["id"]); ?>,<?php echo ($vo["num"]); ?>,<?php echo ($vo["price"]); ?>,<?php echo $_GET['idc']; ?>);" value="确定"></a><input type="button" onclick='resets()' value="取消"></a></div></div></div><div class="jz"><?php echo $vo['price']*$vo['num']; ?>大卡</div><div class="delete"><a href="javascript:void(0);" onclick="del(<?php echo ($vo["id"]); ?>,<?php echo $_GET['idc']; ?>);"></a></div></dd></dl><?php endforeach; endif; else: echo "$empty" ;endif; ?></div>